import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { BannerComponent } from "../../banner/banner.component";
import { LoadingComponent } from "../../loading/loading.component";
import { Observable, take } from 'rxjs';
import { Course } from '../../../shared/entities/course';
import { HomeApiService } from './home-api.service';
import { RollingCodeComponent } from "../../rolling-code/rolling-code.component";
import { FooterComponent } from "../../footer/footer.component";
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { Store } from '@ngrx/store';
import { User } from '../../../shared/entities/user';
import { Sesion } from '../../ngrx/auth/auth.model';
import { ModalAddFormInformationComponent } from '../../modal-add-form-information/modal-add-form-information.component';
declare const swal: any;

@Component({
  selector: 'app-home',
  imports: [CommonModule, BannerComponent, LoadingComponent, RollingCodeComponent, FooterComponent],
  templateUrl: './home.component.html',
  styleUrl: './home.component.css'
})
export class HomeComponent {

  courses$!: Observable<Course[]>;
  usuario$: Observable<User | null>;

  constructor(
    private homeApiService: HomeApiService,
    private modalService: NgbModal,
    private store: Store<{ sesion: Sesion }>
  ) {
    this.usuario$ = this.store.select(state => state.sesion.usuarioLogueado);
  }

  ngOnInit(): void {
    this.courses$ = this.homeApiService.getCursos();
  }

  abrirModal(courseId: string) {
    this.usuario$.pipe(take(1)).subscribe(usuario => {

      const modalRef = this.modalService.open(ModalAddFormInformationComponent);

      modalRef.componentInstance.courseId = courseId;
      modalRef.componentInstance.usuario = usuario;

      modalRef.closed.subscribe((data) => {
        if (data) {
          this.homeApiService.createInformation(data).subscribe({
            next: () => {
              swal({
                title: "Â¡Solicitud enviada!",
                text: "Tu informaciÃ³n fue enviada correctamente. Pronto nos contactaremos contigo.",
                icon: "success",
                button: "Aceptar"
              });
            },
            error: () => {
              swal({
                title: "Error",
                text: "Hubo un problema al enviar la informaciÃ³n. Intenta nuevamente.",
                icon: "error",
                button: "Aceptar"
              });
            }
          });
        }
      });

    });
  }

}

import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { Observable, of, switchMap } from 'rxjs';
import { InformacionServiceService } from './informacion-service.service';
import { IInformation } from '../../../shared/entities/information';
import { TableInformationComponent } from "../../table-information/table-information.component";
import { LoadingComponent } from "../../loading/loading.component";
import { ModalEditFormInformationComponent } from "../../modal-edit-form-information/modal-edit-form-information.component";
import { EdteamComponent } from "../../edteam/edteam.component";
import { FooterComponent } from "../../footer/footer.component";
declare const swal: any;

@Component({
  selector: 'app-informacion',
  imports: [CommonModule, TableInformationComponent, LoadingComponent, EdteamComponent, FooterComponent],
  templateUrl: './informacion.component.html',
  styleUrl: './informacion.component.css'
})
export class InformacionComponent implements OnInit {

  information$!: Observable<IInformation[]>;

  constructor(
    private infoService: InformacionServiceService,
    private modalService: NgbModal
  ) {}

  ngOnInit(): void {
    this.loadInformation();
  }

  loadInformation() {
    this.information$ = this.infoService.getAll();
  }

  openEditModal(info: IInformation): void {
    const modalRef = this.modalService.open(ModalEditFormInformationComponent, { size: 'lg' });
    modalRef.componentInstance.info = info;

    modalRef.result.then((updatedInfo: IInformation) => {
      if (updatedInfo) {
        this.infoService.update(updatedInfo._id!, updatedInfo).pipe(
          switchMap(() => {
            this.loadInformation();
            return of(null);
          })
        ).subscribe({
          next: () => swal('Ã‰xito', 'Datos actualizados correctamente.', 'success'),
          error: () => swal('Error', 'OcurriÃ³ un error al actualizar.', 'error')
        });
      }
    }).catch(() => {});
  }

  onInformationDeleted(info: IInformation): void {
    swal({
      title: 'Â¿Seguro?',
      text: `Eliminar: ${info.firstName} ${info.lastName}`,
      icon: 'warning',
      buttons: ['Cancelar', 'Eliminar'],
      dangerMode: true
    }).then((willDelete: boolean) => {
      if (willDelete) {
        this.infoService.delete(info._id!).pipe(
          switchMap(() => {
            this.loadInformation();
            return of(null);
          })
        ).subscribe({
          next: () => swal('Eliminado', 'Registro eliminado correctamente.', 'success'),
          error: () => swal('Error', 'No se pudo eliminar.', 'error')
        });
      }
    });
  }

  onConvert(info: IInformation) {
    this.infoService.convertToUser(info._id!).pipe(
      switchMap(() => {
        this.loadInformation();
        return of(null);
      })
    ).subscribe({
      next: () => swal('Ã‰xito', 'Convertido en usuario.', 'success'),
      error: () => swal('Error', 'No se pudo convertir.', 'error')
    });
  }
}

