import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { NgbActiveModal, NgbModule } from '@ng-bootstrap/ng-bootstrap';
import { CommonModule } from '@angular/common';
import { LoginApiService } from './login-api.service';
import { User } from '../../shared/entities/user';
declare const swal: any;

@Component({
  selector: 'app-modal-iniciar-sesion',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, NgbModule],
  templateUrl: './modal-iniciar-sesion.component.html',
  styleUrls: ['./modal-iniciar-sesion.component.css']
})
export class ModalIniciarSesionComponent implements OnInit {
  loginForm!: FormGroup;

  constructor(
    private fb: FormBuilder,
    private loginApi: LoginApiService,
    public activeModal: NgbActiveModal
  ) {}

  ngOnInit(): void {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [
        Validators.required,
        Validators.minLength(6),
        Validators.maxLength(16),
        Validators.pattern(/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,16}$/)
      ]]
    });
  }

  close(): void {
    this.activeModal.dismiss('cancel');
  }

  onSubmit(): void {
    if (!this.loginForm.valid) {
      swal('Error', 'Complete correctamente el formulario', 'error');
      return;
    }

    const { email, password } = this.loginForm.value;

    this.loginApi.login(email, password).subscribe({
      next: (res) => {
        const usuario: User = res.data; // ðŸ‘ˆ aseguramos que es del tipo User
        swal('Ã‰xito', res.message || 'Inicio de sesiÃ³n exitoso', 'success');
        this.activeModal.close(usuario);
      },
      error: (err) => {
        console.error('Error en login:', err);
        swal('Error', err.error?.message || 'Credenciales incorrectas', 'error');
      }
    });
  }
}

import { Component, OnInit } from '@angular/core';
import { RouterModule } from '@angular/router';
import { RoutePaths } from '../../shared/routes';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { ModalIniciarSesionComponent } from "../modal-iniciar-sesion/modal-iniciar-sesion.component";
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { User } from '../../shared/entities/user';
import { CommonModule } from '@angular/common';
import { ModalEditFormPerfilComponent } from '../modal-edit-form-perfil/modal-edit-form-perfil.component';
import { cerrarSesion, iniciarSesion } from '../ngrx/auth/auth.actions';
import { Sesion } from '../ngrx/auth/auth.model';
import { FullnamePipe } from '../../shared/pipes/fullname.pipe';
declare const swal: any;

@Component({
  selector: 'app-navbar',
  imports: [CommonModule, RouterModule, FullnamePipe],
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  routePaths = RoutePaths;
  usuario$: Observable<User | null>;

  constructor(private modalService: NgbModal, private store: Store<{ sesion: Sesion }>) {
    this.usuario$ = this.store.select(state => state.sesion.usuarioLogueado);
  }

  ngOnInit() {
    if (typeof localStorage !== 'undefined') {
      const usuarioGuardado = localStorage.getItem('usuarioLogueado');
      if (usuarioGuardado) {
        const usuario = JSON.parse(usuarioGuardado);
        this.store.dispatch(iniciarSesion({ usuario }));
      }
    }
  }

  abrirModal() {
    const modalRef = this.modalService.open(ModalIniciarSesionComponent, { centered: true });

    modalRef.result.then(
      (usuario) => {
        if (usuario && typeof localStorage !== 'undefined') {
          localStorage.setItem('usuarioLogueado', JSON.stringify(usuario));
          this.store.dispatch(iniciarSesion({ usuario }));

          swal({
            title: `Â¡Bienvenido, ${usuario.email}!`,
            icon: 'success',
            timer: 2000,
            buttons: false
          });
        }
      },
      (reason) => {
        if (reason !== 'cancel' && reason !== 'Cross click' && reason !== 0) {
          swal({
            title: 'Error',
            text: 'No se iniciÃ³ sesiÃ³n',
            icon: 'error',
            timer: 2000,
            buttons: false
          });
        }
      }
    );
  }


  cerrarSesion() {
    swal({
      title: "Â¿EstÃ¡s seguro?",
      text: "Se cerrarÃ¡ tu sesiÃ³n actual.",
      icon: "warning",
      buttons: ["Cancelar", "SÃ­, cerrar sesiÃ³n"],
      dangerMode: true,
    }).then((confirmar: boolean) => {
      if (confirmar && typeof localStorage !== 'undefined') {
        localStorage.removeItem('usuarioLogueado');
        this.store.dispatch(cerrarSesion());
        swal({
          title: "SesiÃ³n cerrada",
          icon: "info",
          timer: 2000,
          buttons: false
        });
      }
    });
  }

  abrirModalPerfil(usuario: User) {
    const modalRef = this.modalService.open(ModalEditFormPerfilComponent, { centered: true });
    modalRef.componentInstance.user = usuario;

    modalRef.result.then(
      (updatedUser) => {
        if (updatedUser && typeof localStorage !== 'undefined') {
          localStorage.setItem('usuarioLogueado', JSON.stringify(updatedUser));
          this.store.dispatch(iniciarSesion({ usuario: updatedUser }));
          swal({
            title: `Â¡Perfil actualizado!`,
            icon: 'success',
            timer: 2000,
            buttons: false
          });
        }
      },
      () => { }
    );
  }

  isMenuVisible = false;

  abrirMenu() {
    this.isMenuVisible = true;
  }

  cerrarMenu() {
    this.isMenuVisible = false;
  }
}

