:root {
    --color-1: #223d58;
    --color-2: #3e6389;
    --color-3: #6e94b9;
    --color-4: #b1d1f1;
    --color-5: #1c7373;
    --color-6: #005757;
    --color-7: #b45908;
    --color-8: #ab5408;
    --color-fondo: #eaeef4;
    --color-complementario-I : #ce1d49;
    --color-complementario-II : #b81941;
    --color-success: #28a745;
    --color-warning: #ffc107;
    --color-danger: #dc3545;
    --color-blanco: #fff;
    --color-negro: #000;
}

header{
    display: flex;
    align-items: center;
    justify-content: center;
    position: sticky;
    top: 0;
    padding: 15px 20px;
    width: 100%;
    height: auto;
    z-index: 10;
    background-color: var(--color-1);
}

header .contenedor{
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
}

.contenedor .logo{
    display: flex;
    align-items: center;
    justify-content: center;
}

.contenedor .logo a{
    display: flex;
    align-items: center;
    justify-content: center;
    text-decoration: none;
}

.contenedor .logo a img{
    width: 50px;
    height: 50px;
    vertical-align: middle;
    filter: drop-shadow(0px 0px 5px var(--color-blanco));
    aspect-ratio: 16/9;
}

.contenedor .logo a p{
    margin-left: 5px;
    font-size: 1.4rem;
    color: var(--color-blanco);
    line-height: 1.5;
    font-weight: bold;
    margin: 0px 0px 0px 10px;
}

.contenedor .menu .head{
    display: none;
}

.contenedor .menu ul{
    list-style: none;
    padding: 0;
    margin: 0;
}

.contenedor .menu > ul > li{
    display: inline-block;
}

.contenedor .menu > ul > li:not(:last-child){
    margin-right: 15px;
}

.contenedor .menu ul li a{
    text-decoration: none;
    text-transform: capitalize;
    font-size: 1rem;
    color: var(--color-blanco);
    line-height: 1.5;
    display: block;
}

.contenedor .menu > ul > li > a{
    padding: 15px 0px;
}

.contenedor .menu ul li.dropdown{
    position: relative;
}

.contenedor .menu > ul > li.dropdown > a{
    padding-right: 15px;
}

.contenedor .menu ul .dropdown i.fa-caret-down{
    font-size: 0.8rem;
    pointer-events: none;
    user-select: none;
    position: absolute;
    color: var(--color-blanco);
    top: calc(50% - 5px);
}

.contenedor .menu > ul > li.dropdown > i.fa-caret-down{
    right: 0;
}

.contenedor .menu ul li.dropdown .submenu{
    position: absolute;
    top: 100%;
    left: 0;
    width: 230px;
    padding: 15px 0px;
    background-color: var(--color-2);
    box-shadow: 0 0 px hsla(0, 0%, 0%, 0.5);
    z-index: 1;
    transform-origin: top;
    transform: scale(0);
    visibility: hidden;
    opacity: 0;
}

.contenedor .menu ul li.dropdown .submenu a{
    padding: 10px 24px;
}

.contenedor .menu ul li.dropdown .submenu span{
    display: block;
    margin-top: 7.5px;
    background-image: linear-gradient(hsl(0, 0%, 100%), hsl(0, 0%, 100%));
    background-size: 0 1px;
    background-repeat: no-repeat;
    background-position: 0 100%;
    transition: background-size 0.5s ease;
}

.contenedor .menu ul li.dropdown .submenu li:hover > a > span{
    background-size: 100% 1px;
}

.contenedor .menu ul li.dropdown:hover > .submenu{
    opacity: 1;
    transform: none;
    visibility: visible;
    transition: all 0.5s ease;
}

.contenedor .header-rigth{
